from collections import deque
from typing import List


class Solution:
    def subArrayRanges(self, nums: List[int]) -> int:
        i = 0
        res = 0
        while i < len(nums):
            j = i
            arr = deque(nums[:i+1])
            
            while j < len(nums):
                res += max(arr) - min(arr)
                j+=1
                arr.popleft()
                if j < len(nums): arr.append(nums[j])
                
            i+=1
        return res
    
